class Value
{
    int value;

    public Value(int value)
    {
	this.value = value;
    }

    public void add(int incr)
    {
	this.value += incr;
    }
}

class Solution
{
    public int numDistinct(String S, String T)
    {
	int s_idx = 0;
	int t_idx = 0;
	
	Value count = new Value(0);

	update_count(S, T, count);	
	
	return count.value;
    }

    public int update_count(String S, String T, Value count)
    {
	if(S == null || T == null || S.length() == 0 || T.length() == 0 ||
	   S.length() < T.length() || count == null)
	{
	    return -1;
	}

	char ch_t = T.charAt(0);
	boolean found = false;

	for(int idx_s = 0; idx_s <= S.length()-T.length(); idx_s ++)
	{
	    if(S.charAt(idx_s) == ch_t)
	    {
		found = true;

		if(T.length() == 1)
		{
		    //System.out.println("S = "+S+", T = "+T);

		    count.add(1);
		}
		else
		{
		    //System.out.println("T = "+T);
		    int ret = update_count(S.substring(idx_s+1), T.substring(1), count);
		}
	    }
	}

	if(found == false)
	{
	    return -1;
	}
	else
	{
	    return 0;
	}
    }
}